	$('ul.traits li:textEquals("андроид")').tooltipster({content: 'Андроид (android): Искусственно созданная родословная синтетических гуманоидов обладающих душой. Наиболее распространены в Нумерии.'});
	$('ul.traits li:textEquals("азаркет")').tooltipster({content: 'Азаркет (azarketi): Земноводные гуманоиды живущие вдоль морей региона Внутреннего моря, по слухам происходящие от народа Ацлантов. Порой известны как "жаберные люди" или низшие ацланты.'});
	$('ul.traits li:textEquals("фечлин")').tooltipster({content: 'Фечлин (fetchling): Гуманоидная родословная сбежавшая на Теневой план и измененная им. Обычно называют себя кайалами.'});
